export class Negociacoes {
    constructor() {
        this.negociacoes = [];
    }
    adicionar(negociacao) {
        this.negociacoes.push(negociacao);
    }
    listar() {
        // return [...this.negociacoes]; // criando um novo objeto na mem√≥ria com 'spread operator'
        return this.negociacoes;
    }
}
